import { Button } from "@/components/ui/button";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "@/components/ui/dialog";
import {
    Form,
    FormControl,
    FormField,
    FormItem,
    FormLabel,
    FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import * as z from "zod";
import { useState, useEffect } from "react";
import type { BudgetItem } from "@/lib/mockData";
import { Plus } from "lucide-react";

const formSchema = z.object({
    description: z.string().min(2, "Description must be at least 2 characters."),
    category: z.string().min(1, "Category is required."),
    estimated_cost: z.coerce.number().min(0, "Cost cannot be negative."),
    actual_cost: z.coerce.number().min(0, "Cost cannot be negative."),
});

interface CreateBudgetItemDialogProps {
    eventId: string;
    itemToEdit?: BudgetItem;
    trigger?: React.ReactNode;
    onItemCreated?: (item: BudgetItem) => void;
    onItemUpdated?: (item: BudgetItem) => void;
}

export function CreateBudgetItemDialog({
    eventId,
    itemToEdit,
    trigger,
    onItemCreated,
    onItemUpdated
}: CreateBudgetItemDialogProps) {
    const [open, setOpen] = useState(false);

    const form = useForm<z.infer<typeof formSchema>>({
        resolver: zodResolver(formSchema) as any,
        defaultValues: {
            description: "",
            category: "General",
            estimated_cost: 0,
            actual_cost: 0,
        },
    });

    useEffect(() => {
        if (itemToEdit) {
            form.reset({
                description: itemToEdit.description,
                category: itemToEdit.category,
                estimated_cost: itemToEdit.estimated_cost,
                actual_cost: itemToEdit.actual_cost,
            });
        } else {
            form.reset({
                description: "",
                category: "General",
                estimated_cost: 0,
                actual_cost: 0,
            });
        }
    }, [itemToEdit, form, open]);

    function onSubmit(values: z.infer<typeof formSchema>) {
        const newItem: BudgetItem = {
            id: itemToEdit ? itemToEdit.id : Math.random().toString(36).substr(2, 9),
            event_id: eventId,
            description: values.description,
            category: values.category,
            estimated_cost: values.estimated_cost,
            actual_cost: values.actual_cost,
        };

        if (itemToEdit) {
            onItemUpdated?.(newItem);
        } else {
            onItemCreated?.(newItem);
        }

        setOpen(false);
        form.reset();
    }

    const CATEGORIES = ["General", "Food", "Marketing", "Logistics", "Venue", "Equipment", "Sponsors"];

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogTrigger asChild>
                {trigger || (
                    <Button size="sm">
                        <Plus className="mr-2 h-4 w-4" /> Add Expense
                    </Button>
                )}
            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>{itemToEdit ? "Edit Budget Item" : "Add Budget Item"}</DialogTitle>
                    <DialogDescription>
                        {itemToEdit ? "Update expense details." : "Track a new expense for the event."}
                    </DialogDescription>
                </DialogHeader>
                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                        <FormField
                            control={form.control}
                            name="description"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Description</FormLabel>
                                    <FormControl>
                                        <Input placeholder="e.g. Printing Costs" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                        <FormField
                            control={form.control}
                            name="category"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Category</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select category" />
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            {CATEGORIES.map(cat => (
                                                <SelectItem key={cat} value={cat}>{cat}</SelectItem>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                        <div className="grid grid-cols-2 gap-4">
                            <FormField
                                control={form.control}
                                name="estimated_cost"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Estimated ($)</FormLabel>
                                        <FormControl>
                                            <Input type="number" min="0" step="0.01" {...field} />
                                        </FormControl>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                            <FormField
                                control={form.control}
                                name="actual_cost"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Actual ($)</FormLabel>
                                        <FormControl>
                                            <Input type="number" min="0" step="0.01" {...field} />
                                        </FormControl>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                        </div>
                        <DialogFooter>
                            <Button type="submit">{itemToEdit ? "Update Expense" : "Add Expense"}</Button>
                        </DialogFooter>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
}

import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';
import { format } from 'date-fns';
import { Calendar as CalendarIcon, Plus } from 'lucide-react';

import { Button } from '@/components/ui/button';
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from '@/components/ui/dialog';
import {
    Form,
    FormControl,
    FormField,
    FormItem,
    FormLabel,
    FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select';
import { Calendar } from '@/components/ui/calendar';
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from '@/components/ui/popover';
import { Textarea } from '@/components/ui/textarea';
import { cn } from '@/lib/utils';
import type { Event } from '@/lib/mockData';

const formSchema = z.object({
    name: z.string().min(2, {
        message: "Event name must be at least 2 characters.",
    }),
    event_type: z.enum(["O-Week", "Basant", "SRC Festival", "Coke Studio", "General"]),
    date_start: z.date(),
    location: z.string().min(2, {
        message: "Location must be at least 2 characters.",
    }),
    budget_total: z.coerce.number().min(1, {
        message: "Budget must be at least 1.",
    }),
    description: z.string().optional(),
    status: z.enum(["planning", "active", "completed", "cancelled"]).optional(),
});

interface CreateEventDialogProps {
    eventToEdit?: Event;
    trigger?: React.ReactNode;
    onEventCreated?: (event: Event) => void;
    onEventUpdated?: (event: Event) => void;
}

export function CreateEventDialog({
    eventToEdit,
    trigger,
    onEventCreated,
    onEventUpdated
}: CreateEventDialogProps) {
    const [open, setOpen] = useState(false);

    const form = useForm({
        resolver: zodResolver(formSchema),
        defaultValues: {
            name: eventToEdit?.name || "",
            event_type: eventToEdit?.event_type || "General",
            location: eventToEdit?.location || "",
            budget_total: eventToEdit?.budget_total || 0,
            description: eventToEdit?.description || "",
            date_start: eventToEdit ? new Date(eventToEdit.date_start) : undefined,
            status: eventToEdit?.status || "planning",
        },
    });

    const calculateStatus = (date: Date): "planning" | "active" | "completed" => {
        const now = new Date();
        const eventDate = new Date(date);

        // Reset times to compare dates only
        now.setHours(0, 0, 0, 0);
        eventDate.setHours(0, 0, 0, 0);

        if (eventDate > now) return "planning";
        if (eventDate.getTime() === now.getTime()) return "active";
        return "completed";
    };

    function onSubmit(values: z.infer<typeof formSchema>) {
        if (eventToEdit) {
            const updatedEvent: Event = {
                ...eventToEdit,
                name: values.name,
                event_type: values.event_type as Event['event_type'],
                date_start: values.date_start.toISOString(),
                date_end: values.date_start.toISOString(),
                location: values.location,
                description: values.description || "",
                budget_total: values.budget_total,
                status: values.status as Event['status'], // Manual override
            };
            if (onEventUpdated) {
                onEventUpdated(updatedEvent);
            }
        } else {
            // Auto-calculate status for new events
            const autoStatus = calculateStatus(values.date_start);

            const newEvent: Event = {
                id: Math.random().toString(36).substr(2, 9),
                name: values.name,
                event_type: values.event_type as Event['event_type'],
                status: autoStatus,
                date_start: values.date_start.toISOString(),
                date_end: values.date_start.toISOString(),
                location: values.location,
                budget_total: values.budget_total,
                budget_spent: 0,
                created_at: new Date().toISOString(),
                description: values.description || "",
            };
            if (onEventCreated) {
                onEventCreated(newEvent);
            }
        }

        setOpen(false);
        form.reset();
    }

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogTrigger asChild>
                {trigger ? trigger : (
                    <Button className="bg-primary hover:bg-primary/90">
                        <Plus className="mr-2 h-4 w-4" /> Create Event
                    </Button>
                )}
            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>{eventToEdit ? "Edit Event" : "Create New Event"}</DialogTitle>
                    <DialogDescription>
                        {eventToEdit
                            ? "Make changes to the event details here. Click save when you're done."
                            : "Enter detailed information for the new event here. Click save when you're done."}
                    </DialogDescription>
                </DialogHeader>
                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                        <FormField
                            control={form.control}
                            name="name"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Event Name</FormLabel>
                                    <FormControl>
                                        <Input placeholder="Annual Gala" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <div className="grid grid-cols-2 gap-4">
                            <FormField
                                control={form.control}
                                name="event_type"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Type</FormLabel>
                                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                                            <FormControl>
                                                <SelectTrigger>
                                                    <SelectValue placeholder="Select type" />
                                                </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                                <SelectItem value="General">General</SelectItem>
                                                <SelectItem value="O-Week">O-Week</SelectItem>
                                                <SelectItem value="Basant">Basant</SelectItem>
                                                <SelectItem value="SRC Festival">SRC Festival</SelectItem>
                                                <SelectItem value="Coke Studio">Coke Studio</SelectItem>
                                            </SelectContent>
                                        </Select>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />

                            {eventToEdit && (
                                <FormField
                                    control={form.control}
                                    name="status"
                                    render={({ field }) => (
                                        <FormItem>
                                            <FormLabel>Status</FormLabel>
                                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                                                <FormControl>
                                                    <SelectTrigger>
                                                        <SelectValue placeholder="Select status" />
                                                    </SelectTrigger>
                                                </FormControl>
                                                <SelectContent>
                                                    <SelectItem value="planning">Planning</SelectItem>
                                                    <SelectItem value="active">Active</SelectItem>
                                                    <SelectItem value="completed">Completed</SelectItem>
                                                    <SelectItem value="cancelled">Cancelled</SelectItem>
                                                </SelectContent>
                                            </Select>
                                            <FormMessage />
                                        </FormItem>
                                    )}
                                />
                            )}
                        </div>

                        <div className="grid grid-cols-1 gap-4">
                            <FormField
                                control={form.control}
                                name="date_start"
                                render={({ field }) => (
                                    <FormItem className="flex flex-col">
                                        <FormLabel>Date</FormLabel>
                                        <Popover>
                                            <PopoverTrigger asChild>
                                                <FormControl>
                                                    <Button
                                                        variant={"outline"}
                                                        className={cn(
                                                            "w-full pl-3 text-left font-normal",
                                                            !field.value && "text-muted-foreground"
                                                        )}
                                                    >
                                                        {field.value ? (
                                                            format(field.value, "PPP")
                                                        ) : (
                                                            <span>Pick a date</span>
                                                        )}
                                                        <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                                                    </Button>
                                                </FormControl>
                                            </PopoverTrigger>
                                            <PopoverContent className="w-auto p-0" align="start">
                                                <Calendar
                                                    mode="single"
                                                    selected={field.value}
                                                    onSelect={field.onChange}
                                                    disabled={(date) =>
                                                        date < new Date() || date < new Date("1900-01-01")
                                                    }
                                                    initialFocus
                                                />
                                            </PopoverContent>
                                        </Popover>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                        </div>

                        <FormField
                            control={form.control}
                            name="location"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Location</FormLabel>
                                    <FormControl>
                                        <Input placeholder="Main Hall" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="budget_total"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Budget ($)</FormLabel>
                                    <FormControl>
                                        <Input
                                            type="number"
                                            placeholder="0.00"
                                            {...field}
                                            value={field.value ? String(field.value) : ""}
                                            onChange={(e) => field.onChange(Number(e.target.value))}
                                        />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="description"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Description</FormLabel>
                                    <FormControl>
                                        <Textarea
                                            placeholder="Brief description of the event..."
                                            className="resize-none"
                                            {...field}
                                        />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <DialogFooter>
                            <Button type="submit">{eventToEdit ? "Save Changes" : "Create Event"}</Button>
                        </DialogFooter>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
}

import { Button } from "@/components/ui/button";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "@/components/ui/dialog";
import {
    Form,
    FormControl,
    FormField,
    FormItem,
    FormLabel,
    FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import * as z from "zod";
import { useState, useEffect } from "react";
import type { InventoryItem, InventoryStatus } from "@/lib/mockData";
import { Plus } from "lucide-react";

const formSchema = z.object({
    name: z.string().min(2, "Item name must be at least 2 characters."),
    quantity: z.coerce.number().min(1, "Quantity must be at least 1."),
    status: z.enum(["needed", "acquired", "available"] as [string, ...string[]]),
});

interface CreateInventoryItemDialogProps {
    eventId: string;
    itemToEdit?: InventoryItem;
    trigger?: React.ReactNode;
    onItemCreated?: (item: InventoryItem) => void;
    onItemUpdated?: (item: InventoryItem) => void;
}

export function CreateInventoryItemDialog({
    eventId,
    itemToEdit,
    trigger,
    onItemCreated,
    onItemUpdated
}: CreateInventoryItemDialogProps) {
    const [open, setOpen] = useState(false);

    const form = useForm<z.infer<typeof formSchema>>({
        resolver: zodResolver(formSchema) as any,
        defaultValues: {
            name: "",
            quantity: 1,
            status: "needed",
        },
    });

    useEffect(() => {
        if (itemToEdit) {
            form.reset({
                name: itemToEdit.name,
                quantity: itemToEdit.quantity,
                status: itemToEdit.status,
            });
        } else {
            form.reset({
                name: "",
                quantity: 1,
                status: "needed",
            });
        }
    }, [itemToEdit, form, open]);

    function onSubmit(values: z.infer<typeof formSchema>) {
        const newItem: InventoryItem = {
            id: itemToEdit ? itemToEdit.id : Math.random().toString(36).substr(2, 9),
            event_id: eventId,
            name: values.name,
            quantity: values.quantity,
            status: values.status as InventoryStatus,
        };

        if (itemToEdit) {
            onItemUpdated?.(newItem);
        } else {
            onItemCreated?.(newItem);
        }

        setOpen(false);
        form.reset();
    }

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogTrigger asChild>
                {trigger || (
                    <Button size="sm">
                        <Plus className="mr-2 h-4 w-4" /> Add Item
                    </Button>
                )}
            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>{itemToEdit ? "Edit Inventory Item" : "Add Inventory Item"}</DialogTitle>
                    <DialogDescription>
                        {itemToEdit ? "Update the details of this item." : "Add a new item to the event inventory."}
                    </DialogDescription>
                </DialogHeader>
                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                        <FormField
                            control={form.control}
                            name="name"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Item Name</FormLabel>
                                    <FormControl>
                                        <Input placeholder="e.g. Tables" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                        <div className="grid grid-cols-2 gap-4">
                            <FormField
                                control={form.control}
                                name="quantity"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Quantity</FormLabel>
                                        <FormControl>
                                            <Input type="number" min="1" {...field} />
                                        </FormControl>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                            <FormField
                                control={form.control}
                                name="status"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Status</FormLabel>
                                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                                            <FormControl>
                                                <SelectTrigger>
                                                    <SelectValue placeholder="Select status" />
                                                </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                                <SelectItem value="needed">Needed</SelectItem>
                                                <SelectItem value="acquired">Acquired</SelectItem>
                                                <SelectItem value="available">Available</SelectItem>
                                            </SelectContent>
                                        </Select>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                        </div>
                        <DialogFooter>
                            <Button type="submit">{itemToEdit ? "Update Item" : "Add Item"}</Button>
                        </DialogFooter>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
}

import { NavLink } from 'react-router-dom';
import { LayoutDashboard, CalendarDays, CheckSquare, Users, Calendar, Handshake, Archive, Settings, Banknote } from 'lucide-react';
import { cn } from '@/lib/utils';
import { Button } from '@/components/ui/button';

export const NAV_ITEMS = [
    { name: 'Dashboard', href: '/dashboard', icon: LayoutDashboard },
    { name: 'Events', href: '/events', icon: CalendarDays },
    { name: 'Meetings', href: '/meetings', icon: Calendar },
    { name: 'Tasks', href: '/tasks', icon: CheckSquare },
    { name: 'Sponsors', href: '/sponsors', icon: Handshake },
    { name: 'Budget', href: '/budget', icon: Banknote },
    { name: 'Team', href: '/team', icon: Users },
    { name: 'Archive', href: '/archive', icon: Archive },
];

export function Sidebar({ className }: { className?: string }) {
    return (
        <div className={cn("pb-12 min-h-screen border-r bg-background", className)}>
            <div className="space-y-4 py-4">
                <div className="px-3 py-2">
                    <div className="mb-2 px-4 flex items-center gap-2">
                        <img src="/pakSocMascot.png" alt="PakSoc Mascot" className="h-8 w-8 object-contain" />
                        <h2 className="text-lg font-semibold tracking-tight text-primary">
                            PakSoc
                        </h2>
                    </div>
                    <div className="space-y-1">
                        {NAV_ITEMS.map((item) => (
                            <NavLink
                                key={item.href}
                                to={item.href}
                                className={({ isActive }) =>
                                    cn(
                                        "flex items-center rounded-md px-3 py-2 text-sm font-medium hover:bg-accent hover:text-accent-foreground transition-colors",
                                        isActive ? "bg-accent/50 text-accent-foreground" : "transparent"
                                    )
                                }
                            >
                                <item.icon className="mr-2 h-4 w-4" />
                                {item.name}
                            </NavLink>
                        ))}
                    </div>
                </div>
                <div className="px-3 py-2">
                    <h2 className="mb-2 px-4 text-lg font-semibold tracking-tight">
                        Settings
                    </h2>
                    <div className="space-y-1">
                        <Button variant="ghost" className="w-full justify-start">
                            <Settings className="mr-2 h-4 w-4" />
                            General
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    );
}

import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';
import { Plus } from 'lucide-react';

import { Button } from '@/components/ui/button';
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from '@/components/ui/dialog';
import {
    Form,
    FormControl,
    FormField,
    FormItem,
    FormLabel,
    FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import type { Task } from '@/lib/mockData';

const formSchema = z.object({
    title: z.string().min(2, {
        message: "Task title must be at least 2 characters.",
    }),
    status: z.enum(["todo", "in_progress", "completed"]),
    assigned_to: z.string().optional(),
    description: z.string().optional(),
    priority: z.enum(["low", "medium", "high"]),
    category: z.enum(["general", "content", "logistics", "food", "props", "sponsors"]),
});

interface CreateTaskDialogProps {
    eventId?: string; // Optional if editing
    taskToEdit?: Task;
    trigger?: React.ReactNode;
    onTaskCreated?: (task: Task) => void;
    onTaskUpdated?: (task: Task) => void;
}

export function CreateTaskDialog({
    eventId,
    taskToEdit,
    trigger,
    onTaskCreated,
    onTaskUpdated
}: CreateTaskDialogProps) {
    const [open, setOpen] = useState(false);

    const form = useForm<z.infer<typeof formSchema>>({
        resolver: zodResolver(formSchema),
        defaultValues: {
            title: taskToEdit?.title || "",
            status: taskToEdit?.status || "todo",
            assigned_to: taskToEdit?.assigned_to || "",
            description: taskToEdit?.description || "",
            priority: taskToEdit?.priority || "medium",
            category: taskToEdit?.category || "general",
        },
    });

    function onSubmit(values: z.infer<typeof formSchema>) {
        if (taskToEdit) {
            const updatedTask: Task = {
                ...taskToEdit,
                title: values.title,
                status: values.status as Task['status'],
                assigned_to: values.assigned_to || "Unassigned",
                priority: values.priority as Task['priority'],
                category: values.category as Task['category'],
                description: values.description,
            };
            if (onTaskUpdated) {
                onTaskUpdated(updatedTask);
            }
        } else if (eventId) {
            const newTask: Task = {
                id: Math.random().toString(36).substr(2, 9),
                event_id: eventId,
                title: values.title,
                status: values.status as Task['status'],
                assigned_to: values.assigned_to || "Unassigned",
                priority: values.priority as Task['priority'],
                category: values.category as Task['category'],
                created_at: new Date().toISOString(),
                description: values.description,
            };

            if (onTaskCreated) {
                onTaskCreated(newTask);
            }
        }

        setOpen(false);
        form.reset();
    }

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogTrigger asChild>
                {trigger ? trigger : (
                    <Button size="sm">
                        <Plus className="mr-2 h-4 w-4" /> Add Task
                    </Button>
                )}
            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>{taskToEdit ? "Edit Task" : "Add New Task"}</DialogTitle>
                    <DialogDescription>
                        {taskToEdit ? "Update task details." : "Create a new task for this event."}
                    </DialogDescription>
                </DialogHeader>
                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                        <FormField
                            control={form.control}
                            name="title"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Title</FormLabel>
                                    <FormControl>
                                        <Input placeholder="Design posters" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="assigned_to"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Assignee</FormLabel>
                                    <FormControl>
                                        <Input placeholder="John Doe" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="status"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Status</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select status" />
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            <SelectItem value="todo">To Do</SelectItem>
                                            <SelectItem value="in_progress">In Progress</SelectItem>
                                            <SelectItem value="completed">Completed</SelectItem>
                                        </SelectContent>
                                    </Select>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <div className="grid grid-cols-2 gap-4">
                            <FormField
                                control={form.control}
                                name="priority"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Priority</FormLabel>
                                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                                            <FormControl>
                                                <SelectTrigger>
                                                    <SelectValue placeholder="Priority" />
                                                </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                                <SelectItem value="low">Low</SelectItem>
                                                <SelectItem value="medium">Medium</SelectItem>
                                                <SelectItem value="high">High</SelectItem>
                                            </SelectContent>
                                        </Select>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />

                            <FormField
                                control={form.control}
                                name="category"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Category</FormLabel>
                                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                                            <FormControl>
                                                <SelectTrigger>
                                                    <SelectValue placeholder="Category" />
                                                </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                                <SelectItem value="general">General</SelectItem>
                                                <SelectItem value="content">Content</SelectItem>
                                                <SelectItem value="logistics">Logistics</SelectItem>
                                                <SelectItem value="food">Food</SelectItem>
                                                <SelectItem value="props">Props</SelectItem>
                                                <SelectItem value="sponsors">Sponsors</SelectItem>
                                            </SelectContent>
                                        </Select>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                        </div>

                        <FormField
                            control={form.control}
                            name="description"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Description</FormLabel>
                                    <FormControl>
                                        <Textarea
                                            placeholder="Task details..."
                                            className="resize-none"
                                            {...field}
                                        />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <DialogFooter>
                            <Button type="submit">{taskToEdit ? "Save Changes" : "Create Task"}</Button>
                        </DialogFooter>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
}

