"use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  FormProvider,
  useFormContext,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue | null>(null)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  if (!itemContext) {
    throw new Error("useFormField should be used within <FormItem>")
  }

  const fieldState = getFieldState(fieldContext.name, formState)

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue | null>(null)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message ?? "") : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

import { useState, useEffect } from "react";
import { DragDropContext, Droppable, Draggable, type DropResult } from "@hello-pangea/dnd";
import { Plus, GripVertical, Calendar as CalendarIcon } from "lucide-react";
import { format } from "date-fns";

import { type ContentIdea, mockApi } from "@/lib/mockData";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ContentForm } from "./ContentForm";
import { toast } from "sonner";

interface ContentBoardProps {
    eventId: string;
}

const COLUMNS = [
    { id: "idea", title: "Idea", color: "bg-yellow-500/10 text-yellow-500" },
    { id: "planned", title: "Planned", color: "bg-blue-500/10 text-blue-500" },
    { id: "in_production", title: "In Production", color: "bg-purple-500/10 text-purple-500" },
    { id: "posted", title: "Posted", color: "bg-green-500/10 text-green-500" },
];

export function ContentBoard({ eventId }: ContentBoardProps) {
    const [content, setContent] = useState<ContentIdea[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        loadContent();
    }, [eventId]);

    const loadContent = async () => {
        setLoading(true);
        try {
            const data = await mockApi.getContentByEvent(eventId);
            setContent(data);
        } catch (error) {
            console.error("Failed to load content", error);
            toast.error("Failed to load content");
        } finally {
            setLoading(false);
        }
    };

    const handleContentCreated = (newContent: ContentIdea) => {
        setContent([...content, newContent]);
    };

    const handleContentUpdated = (updatedContent: ContentIdea) => {
        setContent(content.map(c => c.id === updatedContent.id ? updatedContent : c));
    };

    const onDragEnd = async (result: DropResult) => {
        const { destination, source, draggableId } = result;

        if (!destination) return;

        if (
            destination.droppableId === source.droppableId &&
            destination.index === source.index
        ) {
            return;
        }

        const newStatus = destination.droppableId as ContentIdea['status'];
        const itemToUpdate = content.find(c => c.id === draggableId);

        if (itemToUpdate) {
            // Optimistic update
            const updatedItem = { ...itemToUpdate, status: newStatus };
            setContent(content.map(c => c.id === draggableId ? updatedItem : c));

            try {
                await mockApi.updateContent(updatedItem);
                toast.success(`Moved to ${COLUMNS.find(c => c.id === newStatus)?.title}`);
            } catch (error) {
                console.error("Failed to update status", error);
                toast.error("Failed to update status");
                // Revert
                setContent(content);
            }
        }
    };

    const getPlatformIcon = (platform: string) => {
        switch (platform) {
            case 'instagram': return ""; // Replace with Lucide icon if preferred
            case 'tiktok': return "";
            case 'facebook': return "";
            default: return "";
        }
    };

    if (loading) {
        return <div>Loading content board...</div>;
    }

    return (
        <div className="h-full">
            <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold">Content Pipeline</h2>
                <ContentForm
                    eventId={eventId}
                    onContentCreated={handleContentCreated}
                    trigger={
                        <Button>
                            <Plus className="mr-2 h-4 w-4" />
                            New Idea
                        </Button>
                    }
                />
            </div>

            <DragDropContext onDragEnd={onDragEnd}>
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4 h-[calc(100vh-300px)] min-h-[500px]">
                    {COLUMNS.map(column => (
                        <div key={column.id} className="flex flex-col h-full bg-muted/50 rounded-lg p-4">
                            <div className="flex items-center justify-between mb-4">
                                <h3 className="font-medium">{column.title}</h3>
                                <Badge variant="secondary" className="ml-2">
                                    {content.filter(c => c.status === column.id).length}
                                </Badge>
                            </div>

                            <Droppable droppableId={column.id}>
                                {(provided) => (
                                    <div
                                        {...provided.droppableProps}
                                        ref={provided.innerRef}
                                        className="flex-1 space-y-3"
                                    >
                                        {content
                                            .filter(c => c.status === column.id)
                                            .map((item, index) => (
                                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                                    {(provided) => (
                                                        <div
                                                            ref={provided.innerRef}
                                                            {...provided.draggableProps}
                                                            {...provided.dragHandleProps}
                                                        >
                                                            <Card className="cursor-grab hover:shadow-md transition-shadow">
                                                                <CardHeader className="p-3 pb-0 space-y-0">
                                                                    <div className="flex justify-between items-start">
                                                                        <Badge variant="outline" className="mb-2">
                                                                            {getPlatformIcon(item.platform)} {item.platform}
                                                                        </Badge>
                                                                        <ContentForm
                                                                            eventId={eventId}
                                                                            contentToEdit={item}
                                                                            onContentUpdated={handleContentUpdated}
                                                                            trigger={
                                                                                <Button variant="ghost" size="icon" className="h-6 w-6">
                                                                                    <span className="sr-only">Edit</span>
                                                                                    <GripVertical className="h-3 w-3" />
                                                                                </Button>
                                                                            }
                                                                        />
                                                                    </div>
                                                                    <CardTitle className="text-sm font-medium leading-tight">
                                                                        {item.title}
                                                                    </CardTitle>
                                                                </CardHeader>
                                                                <CardContent className="p-3 pt-2">
                                                                    {item.scheduled_date && (
                                                                        <div className="flex items-center text-xs text-muted-foreground mt-2">
                                                                            <CalendarIcon className="mr-1 h-3 w-3" />
                                                                            {format(new Date(item.scheduled_date), "MMM d")}
                                                                        </div>
                                                                    )}
                                                                </CardContent>
                                                            </Card>
                                                        </div>
                                                    )}
                                                </Draggable>
                                            ))}
                                        {provided.placeholder}
                                    </div>
                                )}
                            </Droppable>
                        </div>
                    ))}
                </div>
            </DragDropContext>
        </div>
    );
}

import { useState, useEffect } from 'react';
import { mockApi, MOCK_TEAM, MOCK_EVENTS, type Meeting } from '@/lib/mockData';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogFooter,
    DialogTrigger,
} from '@/components/ui/dialog';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select';
import { Plus } from 'lucide-react';
import { toast } from 'sonner';

interface MeetingFormProps {
    onMeetingCreated?: (meeting: Meeting) => void;
    onMeetingUpdated?: (meeting: Meeting) => void;
    trigger?: React.ReactNode;
    initialData?: Meeting;
}

export function MeetingForm({ onMeetingCreated, onMeetingUpdated, trigger, initialData }: MeetingFormProps) {
    const [open, setOpen] = useState(false);
    const [loading, setLoading] = useState(false);

    const [formData, setFormData] = useState({
        title: initialData?.title || '',
        date: initialData?.date || '',
        location: initialData?.location || '',
        meeting_link: initialData?.meeting_link || '',
        agenda: initialData?.agenda || '',
        event_id: initialData?.event_id || 'none',
        attendees: initialData?.attendees || [],
    });

    // Reset form when opening if not editing, or update if initialData changes
    useEffect(() => {
        if (initialData) {
            setFormData({
                title: initialData.title,
                date: initialData.date,
                location: initialData.location,
                meeting_link: initialData.meeting_link || '',
                agenda: initialData.agenda || '',
                event_id: initialData.event_id || 'none',
                attendees: initialData.attendees,
            });
        }
    }, [initialData, open]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!formData.title || !formData.date || !formData.location) {
            toast.error("Please fill in all required fields");
            return;
        }

        setLoading(true);
        try {
            if (initialData) {
                const updatedMeeting = await mockApi.updateMeeting({
                    ...initialData,
                    title: formData.title,
                    date: new Date(formData.date).toISOString(),
                    location: formData.location,
                    meeting_link: formData.meeting_link,
                    agenda: formData.agenda,
                    attendees: formData.attendees,
                    event_id: formData.event_id === 'none' ? undefined : formData.event_id,
                });
                toast.success("Meeting updated successfully");
                onMeetingUpdated?.(updatedMeeting);
            } else {
                const newMeeting = await mockApi.createMeeting({
                    title: formData.title,
                    date: new Date(formData.date).toISOString(),
                    location: formData.location,
                    meeting_link: formData.meeting_link,
                    agenda: formData.agenda,
                    attendees: formData.attendees,
                    event_id: formData.event_id === 'none' ? undefined : formData.event_id,
                });
                toast.success("Meeting scheduled successfully");
                onMeetingCreated?.(newMeeting);
            }
            setOpen(false);
            if (!initialData) {
                setFormData({
                    title: '',
                    date: '',
                    location: '',
                    meeting_link: '',
                    agenda: '',
                    event_id: 'none',
                    attendees: [],
                });
            }
        } catch (error) {
            toast.error("Failed to schedule meeting");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogTrigger asChild>
                {trigger || (
                    <Button className="gap-2">
                        <Plus className="h-4 w-4" />
                        Schedule Meeting
                    </Button>
                )}
            </DialogTrigger>
            <DialogContent className="sm:max-w-[525px]">
                <form onSubmit={handleSubmit}>
                    <DialogHeader>
                        <DialogTitle>{initialData ? "Edit Meeting" : "Schedule New Meeting"}</DialogTitle>
                    </DialogHeader>
                    <div className="grid gap-4 py-4">
                        <div className="grid gap-2">
                            <Label htmlFor="title">Meeting Title *</Label>
                            <Input
                                id="title"
                                placeholder="e.g. Weekly Sync"
                                value={formData.title}
                                onChange={e => setFormData({ ...formData, title: e.target.value })}
                                required
                            />
                        </div>
                        <div className="grid grid-cols-2 gap-4">
                            <div className="grid gap-2">
                                <Label htmlFor="date">Date & Time *</Label>
                                <Input
                                    id="date"
                                    type="datetime-local"
                                    value={formData.date}
                                    onChange={e => setFormData({ ...formData, date: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="grid gap-2">
                                <Label htmlFor="location">Location *</Label>
                                <Input
                                    id="location"
                                    placeholder="e.g. Zoom, Room 302"
                                    value={formData.location}
                                    onChange={e => setFormData({ ...formData, location: e.target.value })}
                                    required
                                />
                            </div>
                        </div>
                        <div className="grid gap-2">
                            <Label htmlFor="link">Meeting Link (Optional)</Label>
                            <Input
                                id="link"
                                placeholder="https://zoom.us/j/..."
                                value={formData.meeting_link}
                                onChange={e => setFormData({ ...formData, meeting_link: e.target.value })}
                            />
                        </div>
                        <div className="grid gap-2">
                            <Label htmlFor="event">Associated Event</Label>
                            <Select
                                value={formData.event_id}
                                onValueChange={val => setFormData({ ...formData, event_id: val })}
                            >
                                <SelectTrigger>
                                    <SelectValue placeholder="Select event" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="none">None</SelectItem>
                                    {MOCK_EVENTS.map(event => (
                                        <SelectItem key={event.id} value={event.id}>{event.name}</SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>
                        <div className="grid gap-2">
                            <Label>Attendees</Label>
                            <div className="flex flex-wrap gap-2 p-2 border rounded-md max-h-32 overflow-y-auto">
                                {MOCK_TEAM.map(member => (
                                    <label key={member.id} className="flex items-center gap-2 text-sm cursor-pointer hover:bg-secondary p-1 rounded transition-colors">
                                        <input
                                            type="checkbox"
                                            checked={formData.attendees.includes(member.id)}
                                            onChange={e => {
                                                const newAttendees = e.target.checked
                                                    ? [...formData.attendees, member.id]
                                                    : formData.attendees.filter(id => id !== member.id);
                                                setFormData({ ...formData, attendees: newAttendees });
                                            }}
                                            className="rounded border-gray-300 text-primary focus:ring-primary"
                                        />
                                        {member.name}
                                    </label>
                                ))}
                            </div>
                        </div>
                        <div className="grid gap-2">
                            <Label htmlFor="agenda">Agenda</Label>
                            <Textarea
                                id="agenda"
                                placeholder="1. Topic one..."
                                className="min-h-[100px]"
                                value={formData.agenda}
                                onChange={e => setFormData({ ...formData, agenda: e.target.value })}
                            />
                        </div>
                    </div>
                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={() => setOpen(false)}>Cancel</Button>
                        <Button type="submit" disabled={loading}>
                            {loading ? "Saving..." : (initialData ? "Save Changes" : "Schedule Meeting")}
                        </Button>
                    </DialogFooter>
                </form>
            </DialogContent>
        </Dialog>
    );
}

import { useEffect, useState } from 'react';
import { mockApi, type Event, type BudgetItem } from '@/lib/mockData';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import {
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableHeader,
    TableRow,
} from '@/components/ui/table';
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
    PieChart,
    Pie,
    Cell
} from 'recharts';

export default function Budget() {
    const [events, setEvents] = useState<Event[]>([]);
    const [budgetItems, setBudgetItems] = useState<{ eventName: string, item: BudgetItem }[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            const eventsData = await mockApi.getEvents();
            setEvents(eventsData);

            // Fetch budget items for all events
            const allItems: { eventName: string, item: BudgetItem }[] = [];
            for (const event of eventsData) {
                const items = await mockApi.getBudgetByEvent(event.id);
                items.forEach(item => {
                    allItems.push({ eventName: event.name, item });
                });
            }
            setBudgetItems(allItems);
            setLoading(false);
        };

        fetchData();
    }, []);

    // Aggregates
    const totalAllocated = events.reduce((sum, e) => sum + e.budget_total, 0);
    const totalSpent = events.reduce((sum, e) => sum + e.budget_spent, 0);
    const remaining = totalAllocated - totalSpent;

    // Charts Data
    const eventSpendingData = events.map(e => ({
        name: e.name,
        Allocated: e.budget_total,
        Spent: e.budget_spent
    }));

    const categorySpending: Record<string, number> = {};
    budgetItems.forEach(({ item }) => {
        const cat = item.category || 'Uncategorized';
        categorySpending[cat] = (categorySpending[cat] || 0) + item.actual_cost;
    });

    const categoryData = Object.entries(categorySpending).map(([name, value]) => ({ name, value }));
    const COLORS = ['#0F9D58', '#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    if (loading) return <div className="p-8">Loading budget dashboard...</div>;

    return (
        <div className="flex flex-col h-full bg-slate-50/50 dark:bg-slate-900/50 p-6 space-y-6">
            <div>
                <h1 className="text-3xl font-bold tracking-tight">Budget Overview</h1>
                <p className="text-muted-foreground mt-1">Financial health across society events</p>
            </div>

            {/* Summary Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <Card>
                    <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                        <CardTitle className="text-sm font-medium">Total Allocated</CardTitle>
                        <span className="text-2xl font-bold">${totalAllocated.toLocaleString()}</span>
                    </CardHeader>
                    <CardContent>
                        <div className="text-xs text-muted-foreground">Across {events.length} events</div>
                    </CardContent>
                </Card>
                <Card>
                    <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                        <CardTitle className="text-sm font-medium">Total Spent</CardTitle>
                        <span className="text-2xl font-bold text-blue-600">${totalSpent.toLocaleString()}</span>
                    </CardHeader>
                    <CardContent>
                        <div className="text-xs text-muted-foreground">
                            {((totalSpent / totalAllocated) * 100).toFixed(1)}% of budget used
                        </div>
                    </CardContent>
                </Card>
                <Card>
                    <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                        <CardTitle className="text-sm font-medium">Remaining</CardTitle>
                        <span className={`text-2xl font-bold ${remaining < 0 ? 'text-red-500' : 'text-green-600'}`}>
                            ${remaining.toLocaleString()}
                        </span>
                    </CardHeader>
                    <CardContent>
                        <div className="text-xs text-muted-foreground">Available funds</div>
                    </CardContent>
                </Card>
            </div>

            {/* Charts Row */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <Card>
                    <CardHeader>
                        <CardTitle>Budget vs Spent per Event</CardTitle>
                    </CardHeader>
                    <CardContent className="h-[300px]">
                        <ResponsiveContainer width="100%" height="100%">
                            <BarChart data={eventSpendingData}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="name" fontSize={12} tickLine={false} axisLine={false} />
                                <YAxis fontSize={12} tickLine={false} axisLine={false} tickFormatter={(value: number) => `$${value}`} />
                                <Tooltip />
                                <Legend />
                                <Bar dataKey="Allocated" fill="#e2e8f0" radius={[4, 4, 0, 0]} />
                                <Bar dataKey="Spent" fill="#0F9D58" radius={[4, 4, 0, 0]} />
                            </BarChart>
                        </ResponsiveContainer>
                    </CardContent>
                </Card>

                <Card>
                    <CardHeader>
                        <CardTitle>Spending by Category</CardTitle>
                    </CardHeader>
                    <CardContent className="h-[300px]">
                        <ResponsiveContainer width="100%" height="100%">
                            <PieChart>
                                <Pie
                                    data={categoryData}
                                    cx="50%"
                                    cy="50%"
                                    labelLine={false}
                                    label={({ name, percent }: any) => `${name} ${(percent * 100).toFixed(0)}%`}
                                    outerRadius={80}
                                    fill="#8884d8"
                                    dataKey="value"
                                >
                                    {categoryData.map((_, index) => (
                                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                    ))}
                                </Pie>
                                <Tooltip formatter={(value: any) => `$${value.toLocaleString()}`} />
                            </PieChart>
                        </ResponsiveContainer>
                    </CardContent>
                </Card>
            </div>

            {/* Transactions Table */}
            <Card>
                <CardHeader>
                    <CardTitle>Recent Expenses</CardTitle>
                </CardHeader>
                <CardContent>
                    <Table>
                        <TableHeader>
                            <TableRow>
                                <TableHead>Description</TableHead>
                                <TableHead>Event</TableHead>
                                <TableHead>Category</TableHead>
                                <TableHead className="text-right">Cost</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody>
                            {budgetItems.slice(0, 10).map(({ item, eventName }, i) => (
                                <TableRow key={i}>
                                    <TableCell className="font-medium">{item.description}</TableCell>
                                    <TableCell>{eventName}</TableCell>
                                    <TableCell>{item.category}</TableCell>
                                    <TableCell className="text-right">${item.actual_cost.toLocaleString()}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </CardContent>
            </Card>
        </div>
    );
}

import { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { mockApi, MOCK_TEAM, type Meeting } from '@/lib/mockData';
import { Button } from '@/components/ui/button';
import { MeetingForm } from '@/features/meetings/MeetingForm';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Textarea } from '@/components/ui/textarea';
import {
    ArrowLeft,
    Calendar,
    MapPin,
    ExternalLink,
    Users,
    FileText,
    ClipboardList,
    Save,
    Pencil,
    Trash2
} from 'lucide-react';
import { format } from 'date-fns';
import { toast } from 'sonner';
import {
    AlertDialog,
    AlertDialogAction,
    AlertDialogCancel,
    AlertDialogContent,
    AlertDialogDescription,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle,
    AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

export default function MeetingDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [meeting, setMeeting] = useState<Meeting | undefined>(undefined);
    const [loading, setLoading] = useState(true);
    const [minutes, setMinutes] = useState("");
    const [attendees, setAttendees] = useState<string[]>([]);

    useEffect(() => {
        if (id) {
            mockApi.getMeetingById(id).then(data => {
                setMeeting(data);
                if (data) {
                    setMinutes(data.minutes || "");
                    setAttendees(data.attendees || []);
                }
                setLoading(false);
            });
        }
    }, [id]);

    const handleSaveMinutes = async () => {
        if (!meeting) return;
        try {
            await mockApi.updateMeeting({ ...meeting, minutes });
            toast.success("Minutes saved successfully");
            setMeeting({ ...meeting, minutes });
        } catch (error) {
            toast.error("Failed to save minutes");
        }
    };

    const toggleAttendance = async (memberId: string) => {
        if (!meeting) return;

        const newAttendees = attendees.includes(memberId)
            ? attendees.filter(id => id !== memberId)
            : [...attendees, memberId];

        setAttendees(newAttendees); // Optimistic update

        try {
            await mockApi.updateMeeting({ ...meeting, attendees: newAttendees });
            toast.info("Attendance updated");
            setMeeting({ ...meeting, attendees: newAttendees });
        } catch (error) {
            setAttendees(attendees); // Revert on error
            toast.error("Failed to update attendance");
        }
    };

    const handleDelete = async () => {
        if (!meeting) return;
        try {
            await mockApi.deleteMeeting(meeting.id);
            toast.success("Meeting deleted successfully");
            navigate('/meetings');
        } catch (error) {
            toast.error("Failed to delete meeting");
        }
    };

    if (loading) return <div className="p-8 text-center text-muted-foreground">Loading meeting details...</div>;
    if (!meeting) return <div className="p-8 text-center text-muted-foreground">Meeting not found.</div>;

    const isUpcoming = new Date(meeting.date) > new Date();

    return (
        <div className="max-w-4xl mx-auto space-y-6">
            {/* Header */}
            <div className="space-y-4">
                <Link to="/meetings" className="flex items-center gap-2 text-sm text-muted-foreground hover:text-primary transition-colors">
                    <ArrowLeft className="h-4 w-4" />
                    Back to Meetings
                </Link>

                <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
                    <div className="space-y-1">
                        <div className="flex items-center gap-3">
                            <h1 className="text-3xl font-bold tracking-tight text-primary">{meeting.title}</h1>
                            <Badge variant={isUpcoming ? "default" : "secondary"}>
                                {isUpcoming ? "Upcoming" : "Past"}
                            </Badge>
                        </div>
                        <div className="flex flex-wrap gap-4 text-sm text-muted-foreground">
                            <div className="flex items-center gap-1">
                                <Calendar className="h-4 w-4" />
                                {format(new Date(meeting.date), "PPP p")}
                            </div>
                            <div className="flex items-center gap-1">
                                <MapPin className="h-4 w-4" />
                                {meeting.location}
                            </div>
                        </div>
                    </div>
                    {meeting.meeting_link && (
                        <Button className="gap-2" asChild>
                            <a href={meeting.meeting_link} target="_blank" rel="noopener noreferrer">
                                <ExternalLink className="h-4 w-4" />
                                Join Meeting
                            </a>
                        </Button>
                    )}

                    <div className="flex gap-2">
                        <MeetingForm
                            initialData={meeting}
                            onMeetingUpdated={(updated) => setMeeting(updated)}
                            trigger={
                                <Button variant="outline" className="gap-2">
                                    <Pencil className="h-4 w-4" />
                                    Edit
                                </Button>
                            }
                        />

                        <AlertDialog>
                            <AlertDialogTrigger asChild>
                                <Button variant="destructive" className="gap-2">
                                    <Trash2 className="h-4 w-4" />
                                    Delete
                                </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                                <AlertDialogHeader>
                                    <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                                    <AlertDialogDescription>
                                        This action cannot be undone. This will permanently delete the meeting
                                        "{meeting.title}" and remove it from our servers.
                                    </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                                    <AlertDialogAction onClick={handleDelete} className="bg-destructive text-destructive-foreground hover:bg-destructive/90">
                                        Delete
                                    </AlertDialogAction>
                                </AlertDialogFooter>
                            </AlertDialogContent>
                        </AlertDialog>
                    </div>
                </div>
            </div>

            <Separator />

            <Tabs defaultValue="minutes" className="w-full">
                <TabsList className="w-full justify-start border-b rounded-none bg-transparent h-auto p-0 space-x-6">
                    <TabsTrigger
                        value="minutes"
                        className="rounded-none border-b-2 border-transparent data-[state=active]:border-primary data-[state=active]:bg-transparent px-0 py-2"
                    >
                        <FileText className="h-4 w-4 mr-2" />
                        Minutes
                    </TabsTrigger>
                    <TabsTrigger
                        value="attendance"
                        className="rounded-none border-b-2 border-transparent data-[state=active]:border-primary data-[state=active]:bg-transparent px-0 py-2"
                    >
                        <Users className="h-4 w-4 mr-2" />
                        Attendance
                    </TabsTrigger>
                    <TabsTrigger
                        value="agenda"
                        className="rounded-none border-b-2 border-transparent data-[state=active]:border-primary data-[state=active]:bg-transparent px-0 py-2"
                    >
                        <ClipboardList className="h-4 w-4 mr-2" />
                        Agenda
                    </TabsTrigger>
                </TabsList>

                <TabsContent value="minutes" className="mt-6 space-y-4">
                    <div className="flex justify-between items-center">
                        <h2 className="text-xl font-semibold">Meeting Minutes</h2>
                        <Button variant="outline" size="sm" className="gap-2" onClick={handleSaveMinutes}>
                            <Save className="h-4 w-4" />
                            Save Changes
                        </Button>
                    </div>
                    <Textarea
                        placeholder="Start typing meeting minutes here..."
                        className="min-h-[400px] text-base leading-relaxed p-4"
                        value={minutes}
                        onChange={(e) => setMinutes(e.target.value)}
                    />
                </TabsContent>

                <TabsContent value="attendance" className="mt-6 space-y-4">
                    <div className="flex justify-between items-center">
                        <h2 className="text-xl font-semibold">Attendance Tracking</h2>
                        <div className="text-sm text-muted-foreground">
                            {attendees.length} / {MOCK_TEAM.length} Present
                        </div>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {MOCK_TEAM.map(member => (
                            <div
                                key={member.id}
                                className={`flex justify-between items-center p-4 border rounded-lg transition-colors cursor-pointer ${attendees.includes(member.id) ? 'bg-primary/5 border-primary/50' : 'hover:bg-accent'
                                    }`}
                                onClick={() => toggleAttendance(member.id)}
                            >
                                <div className="flex items-center gap-3">
                                    <div className={`w-2 h-2 rounded-full ${attendees.includes(member.id) ? 'bg-primary' : 'bg-muted'}`} />
                                    <div>
                                        <div className="font-medium">{member.name}</div>
                                        <div className="text-xs text-muted-foreground">{member.role}</div>
                                    </div>
                                </div>
                                <div className={`text-xs font-semibold px-2 py-1 rounded ${attendees.includes(member.id)
                                    ? 'bg-primary/10 text-primary'
                                    : 'bg-muted text-muted-foreground'
                                    }`}>
                                    {attendees.includes(member.id) ? 'Present' : 'Absent'}
                                </div>
                            </div>
                        ))}
                    </div>
                </TabsContent>

                <TabsContent value="agenda" className="mt-6 space-y-4">
                    <h2 className="text-xl font-semibold">Meeting Agenda</h2>
                    <div className="p-6 border rounded-lg bg-slate-50 min-h-[200px] whitespace-pre-wrap leading-relaxed">
                        {meeting.agenda || "No agenda set for this meeting."}
                    </div>
                </TabsContent>
            </Tabs>
        </div>
    );
}
